    private static int findSum(int[] arr) {
        //
        int sum = 0;
        for (int j : arr) {
            sum += j;
        }
        return sum;
    }

    private static int findSumUsingStream(int[] arr) {
      return Arrays.stream(arr).sum();
    }

    private static int findSumUsingStream2(int[] arr) {
        AtomicInteger sum = new AtomicInteger();
        Arrays.stream(arr).forEach(sum::addAndGet);
        return sum.get();
    }
